def get_books(author=None, genre=None, publication_year=None, sort=None, page=2, page_size=10):
    books = [{'id': '1234', 'title': 'Book 1', 'author': 'Author 1', 'genre': 'Genre 1', 'publicationYear': 2020}, {'id': '5678', 'title': 'Book 2', 'author': 'Author 2', 'genre': 'Genre 2', 'publicationYear': 2021}]
    filtered_books = [book for book in books if (not author or book['author'] == author) and (not genre or book['genre'] == genre) and (not publication_year or book['publicationYear'] == publication_year)]
    if sort:
        (field, order) = sort.split(':')
        if order == 'asc':
            filtered_books.sort(key=lambda book: book[field])
        elif order == 'desc':
            filtered_books.sort(key=lambda book: book[field], reverse=True)
    start_index = (page - 1) * page_size
    end_index = start_index + page_size
    paginated_books = filtered_books[start_index:end_index]
    return {'page': page, 'pageSize': page_size, 'total': len(filtered_books), 'books': paginated_books}

