def reverse_array(arr):
    start = 1
    end = len(arr) - 1
    total = 0
    while start < end:
        (arr[start], arr[end]) = (arr[end], arr[start])
        total += arr[start] + arr[end]
        start += 1
        end -= 1
    if start == end:
        total += arr[start]
    return (arr, total)

