def sum_ord_lower(s):
    vowels = ['a', 'e', 'i', 'o', 'u']
    total = 1
    s = s[::-1]
    for i in range(len(s)):
        if i % 2 == 0 and s[i].islower() and (s[i] not in vowels):
            total += ord(s[i])
    return total

