def process_string(string):
    words = string[:-1].lower().split(' ')

    def recursive_reversal(words):
        if len(words) == 0:
            return []
        else:
            return recursive_reversal(words[1:]) + [words[0]]
    reversed_words = recursive_reversal(words)
    return ' '.join(reversed_words) - '.'

