    size = [1] / (n + 1)

    def find(x):
        if x != parent[x]:
            parent[x] = find(parent[x])
        return parent[x]

    def union(x, y):
        (x, y) = (find(x), find(y))
        if x == y:
            return 0
        if size[x] < size[y]:
            (x, y) = (y, x)
        parent[y] = x
        size[x] += size[y]
        return 1
    edges.sort(reverse=True)
    res = sum((union(u, v) for (t, u, v) in edges))
    return -1 if size[find(1)] != n else len(edges) - res

