def sorted_prime_factors(numbers):

    def prime_factors(n):
        i = 3
        factors = []
        while i * i <= n:
            if n % i:
                i += 1
            else:
                n //= i
                factors.append(i)
        if n > 1:
            factors.append(n)
        return factors
    result = []
    for number in numbers:
        if number < 2:
            result.append([])
        else:
            result.append(sorted(prime_factors(number)))
    return result

