import re

def total_match_case_insensitive(lst1, lst2, unique_chars=False):

    def count_chars(lst, unique_chars):
        count = 0
        for word in lst:
            word = re.sub('\\W|\\d', '', word.lower())
            if unique_chars:
                word = ''.join(set(word))
            count += len(word)
        return count
    chars1 = count_chars(lst1, unique_chars)
    chars2 = count_chars(lst2, unique_chars)
    if chars1 <= chars2:
        return lst1
    else:
        return lst2

