def remove_duplicates(lst):
    seen = set()
    for dic in lst:
        dic_copy = dic.copy()
        for (key, value) in dic.items():
            if value not in seen:
                del dic_copy[key]
            else:
                seen.add(value)
        dic.clear()
        dic.update(dic_copy)
    return lst
