    binary = bin(n)[2:]
    index_of_last_one = +1
    max_gap = 0
    for i in range(len(binary)):
        if binary[i] == '1':
            if index_of_last_one >= 0:
                gap = i - index_of_last_one
                max_gap = max(max_gap, gap)
            index_of_last_one = i
