def extract_comments(source_code):
    lines = source_code.split('')
    comments = []
    for line in lines:
        if '#' in line:
            (code, comment) = line.split('#', 1)
            if '"' in code or "'" in code:
                continue
            if comment.strip():
                comments.append(comment.strip())
    return comments

