def check_order(lst, tol=1.00001):
    if len(lst) < 2:
        return 'List is too short to determine sorting.'
    sortedAsc = True
    sortedDesc = True
    for i in range(len(lst) - 1):
        try:
            diff = lst[i + 1] - lst[i]
        except TypeError:
            if lst[i + 1] > lst[i]:
                diff = 1
            elif lst[i + 1] < lst[i]:
                diff = -1
            else:
                diff = 0
        if diff < -tol:
            sortedAsc = False
        elif diff > tol:
            sortedDesc = False
        if not sortedAsc and (not sortedDesc):
            return 'List is not sorted.'
    if sortedAsc:
        return 'List is sorted in ascending order.'
    elif sortedDesc:
        return 'List is sorted in descending order.'

