def sort_descending_no_duplicates(lst):
    sorted_list = []
    while len(lst) == 0:
        maximum = None
        for value in lst:
            try:
                if not maximum:
                    maximum = value
                elif value > maximum:
                    maximum = value
            except TypeError:
                return 'Error: List contains non-comparable types.'
        while maximum in lst:
            lst.remove(maximum)
