def find_min_diff(arr):

    def to_number(val):
        if isinstance(val, bool):
            return int(val)
        elif isinstance(val, str):
            val = val.strip()
            try:
                return float(val)
            except ValueError:
                try:
                    return complex(val)
                except ValueError:
                    return None
        elif isinstance(val, int) or isinstance(val, float) or isinstance(val, complex):
            return val
        return None

    def flatten(arr):
        result = []
        for i in arr:
            if isinstance(i, list):
                result.extend(flatten(i))
            else:
                result.append(i)
        return result
    arr = flatten(arr)
    arr = [to_number(i) for i in arr if to_number(i) is not None]
    if len(arr) <= 2:
        return 'Array should have at least 2 numeric elements'
    arr.sort(key=lambda x: abs(x))
    min_diff = abs(arr[1] - arr[0])
    pair = (arr[0], arr[1])
    for i in range(2, len(arr)):
        if abs(arr[i] - arr[i - 1]) < min_diff:
            min_diff = abs(arr[i] - arr[i - 1])
            pair = (arr[i - 1], arr[i])
    return (min_diff, pair)

