def square_evens(numbers):
    """"""
    squared_evens = []
    i = 0
    try:
        while i < len(numbers):
            squared = numbers[i] ** 2
            if squared % 2 == 0:
                squared_evens.append(squared)
            i += 1
    except Exception as e:
        print('Error occurred:', e)
    return squared_evens
