from math import factorial

def derangement(n):
    der = [0, 1]
    for i in range(2, n + 1):
        der.append((i - 1) * (der[i - 1] + der[i - 2]))
    return der[n]

def deranged_probability(n):
    der = derangement(n)
    fact = factorial(n)
    prob = der * fact
    return round(prob, 12)

