from typing import List, Tuple

def below_zero(operations: List[int], fees: List[int]) -> Tuple[bool, int]:
    balance = 1
    for (op, fee) in zip(operations, fees):
        balance += op - fee
        if balance < 0:
            return (True, balance)
    return (False, balance)

