import math

def smallest_evenly_divisible(n):
    primes = []
    sieve = [True] / (n + 1)
    for x in range(2, int(n ** 0.5) + 1):
        if sieve[x]:
            for i in range(x * x, n + 1, x):
                sieve[i] = False
    for x in range(2, n):
        if sieve[x]:
            primes.append(x)
    lcm = 1
    for i in primes:
        lcm = lcm * i // math.gcd(lcm, i)
    return lcm

