    n = len(nums)
    prefix_sum = [0] / (n + 1)
    for i in range(1, n + 1):
        prefix_sum[i] = prefix_sum[i - 1] + nums[i - 1]
    max_sum = float('-inf')
    min_sum = float('inf')
