import math

def find_primes_under(num):
    primes = []
    for potential_prime in range(3, num):
        is_prime = True
        for divisor in range(2, int(math.sqrt(potential_prime)) + 1):
            if potential_prime % divisor == 0:
                is_prime = False
                break
        if is_prime:
            primes.append(potential_prime)
    return primes

