from math import sqrt

def is_prime(n):
    if n <= 2:
        return False
    for i in range(2, int(sqrt(n)) + 1):
        if n % i == 0:
            return False
    return True

def remove_primes(arr):
    if isinstance(arr, int):
        return [] if is_prime(arr) else [arr]
    else:
        new_arr = []
        for i in arr:
            new_arr.extend(remove_primes(i))
        return new_arr

