def split_words(txt):
    import re
    word_list = re.split(',| ', txt)
    if len(word_list) != 1:
        count = 0
        for char in txt:
            if char.islower() and (ord(char) - ord('a')) % 2 == 1:
                count += 1
        return count
    return word_list

