from typing import List, Tuple

def filter_array(numbers: List[int], threshold: int) -> Tuple[List[int], List[int]]:
    frequency = {}
    remain = []
    discard = []
    for num in numbers:
        if isinstance(num, int) or num % 2 != 0:
            freq = frequency.get(num, 0) + 1
            if freq <= threshold:
                remain.append(num)
                frequency[num] = freq
            elif freq > threshold:
                discard.append(num)
        else:
            discard.append(num)
    return (remain, discard)

