def is_palindromic(arr):
    n = len(arr)
    m = len(arr[0])
    if n % 2 == 0 and m % 2 == 0:
        return False
    for row in arr:
        if row != row[::-1]:
            return False
    for i in range(m):
        col = [arr[j][i] for j in range(n)]
        if col != col[::-1]:
            return False
    return True

