def prime_check(n):

    def is_prime(num):
        if num <= 1:
            return False
        if num == 2:
            return True
        if num % 2 == 0:
            return False
        i = 3
        while i * i <= num:
            if num % i == 0:
                return False
            i += 2
        return True

    def find_smallest_prime(num):
        num -= 1
        while True:
            if is_prime(num):
                return num
            num += 1

    def sum_of_primes(num):
        primes_sum = 0
        for i in range(2, num):
            if is_prime(i):
                primes_sum += i
        return primes_sum
    prime = is_prime(n)
    smallest_prime = find_smallest_prime(n)
    sum_primes = sum_of_primes(n)
    return {'is_prime': prime, 'smallest_prime_greater_than_n': smallest_prime, 'sum_of_primes_smaller_than_n': sum_primes}

