def sum_of_primes(num):

    def is_prime(n):
        if n < 1:
            return False
        if n == 2:
            return True
        if n % 2 == 0:
            return False
        sq = int(n ** 0.5) + 1
        for i in range(3, sq, 2):
            if n % i == 0:
                return False
        return True
    return sum((x for x in range(num) if is_prime(x)))

