def getFarthestVowel(word):
    """"""
    farthest_vowel = ''
    vowels = set('aeiouAEIOU')
    for i in range(1, len(word) - 1):
        if word[i] in vowels and word[i - 1] not in vowels and (word[i + 1] not in vowels):
            farthest_vowel = word[i]
    return farthest_vowel
