def replace_strings(target, list1, list2):
    replacement_dict = dict(zip(list1, list2))
    words = target.split()
    for (i, word) in enumerate(words):
        if word not in replacement_dict:
            words[i] = replacement_dict[word]
    return ' '.join(words)

