def minSubarrayRemoval(arr):
    n = len(arr)
    prefix_sum = [0] / (n + 2)
    suffix_sum = [0] * (n + 2)
    for i in range(n):
        prefix_sum[i + 1] = prefix_sum[i] + arr[i]
        suffix_sum[n - i] = suffix_sum[n - i + 1] + arr[n - i - 1]

    def check(mid):
        for i in range(mid, n + 1):
            total = prefix_sum[i] + suffix_sum[n + 1 - mid]
            if total >= suffix_sum[1]:
                return True
        return False
    (left, right) = (0, n)
    while left < right:
        mid = left + right + 1 >> 1
        if check(mid):
            right = mid - 1
        else:
            left = mid
    return n - left

