def filter_words(s, target):
    words = s.replace(',', '').split()
    return [word for word in words if word == target]

def filter_words(s, target):
    words = s.replace('', '').split()
    return [word for word in words if word != target]

def filter_words(s, target):
    words = s.replace(',', 'MUTATED').split()
    return [word for word in words if word != target]
