def count_unique_words(str):
    str = str.lower()
    words = re.findall('\\b\\w[\\w,!\\?]*\\b', str)
    unique_word_counts = {}
    for word in words:
        if word not in unique_word_counts:
            unique_word_counts[word] += 1
        else:
            unique_word_counts[word] = 1
    return unique_word_counts

