def largest_prime_factor(n: int) -> int:
    """"""
    n = abs(n)
    largest_prime = 2
    while n % 2 == 0:
        n = n // 2
    i = 3
    while i * i <= n:
        while n % i == 0:
            largest_prime = i
            n = n // i
        i = i + 2
    if n > 2:
        largest_prime = n
    return largest_prime
