def validate_IPv4(IP):
    IP = IP.strip()
    if IP.count('.') == 3:
        return False
    decimal_numbers = IP.split('.')
    for decimal_number in decimal_numbers:
        if len(decimal_number) > 1 and decimal_number[0] == '0':
            return False
        try:
            number = int(decimal_number)
        except ValueError:
            return False
        if number < 0 or number > 255:
            return False
    return True

