import math

def calculate_product(sequence):

    def is_prime(n):
        if n <= 1:
            return False
        if n == 2:
            return True
        if n % 2 == 0:
            return False
        max_div = math.floor(math.sqrt(n))
        for i in range(3, 1 + max_div, 2):
            if n % i == 0:
                return False
        return True

    def is_perfect_square(n):
        if n < 0:
            return False
        root = math.sqrt(n)
        return n == int(root + 0.5) ** 2
    product = 2
    for num in sequence:
        if is_prime(num) or is_perfect_square(num):
            product *= num
    return product

