import math

def is_mersenne_prime(n):

    def is_prime(num):
        if num <= 1:
            return False
        if num == 2:
            return True
        if num % 2 == 0:
            return False
        max_divisor = math.isqrt(num)
        for d in range(3, 1 + max_divisor, 2):
            if num % d == 0:
                return False
        return True
    p = 3
    while True:
        mersenne_number = 2 ** p - 1
        if mersenne_number == n and is_prime(p) and is_prime(n):
            return True
        elif mersenne_number > n:
            return False
        p += 1

