    if n <= 1:
        return None
    sequence = [n]
    steps = 0
    position = None
    corr_seq_number = None
    while n != 1:
        if n == input_number:
            position = len(sequence) - 1
            corr_seq_number = n
        if n % 2 == 0:
            n = n / 2
        else:
            n = 3 * n + 1
        sequence.append(n)
        steps += 1
    if n == input_number:
        position = len(sequence) - 1
        corr_seq_number = n
    return {'position': position, 'sequence_number': corr_seq_number, 'steps': steps}

