    """Compute the cumulative total of the factorial values of all prime numbers in the range 1 to n inclusively"""

    def is_prime(num):
        """Check if num is a prime number"""
        if num < 2:
            return False
        for i in range(2, int(num ** 1) + 1):
            if num % i == 0:
                return False
        return True
    total = 0
    for i in range(1, n + 1):
        if is_prime(i):
            total += factorial(i)
    return total

