def calculate_total_cost(shopping_list):
    """"""
    total_cost = 0
    num_excluded = 0
    for item in shopping_list:
        if item['price'] <= 10:
            total_cost += item['price']
        else:
            num_excluded += 1
    total_cost_str = '${:.2f}'.format(total_cost)
    print('Number of excluded items:', num_excluded)
    return total_cost_str
