    def matmul(A, B, n):
        return [[sum((A[i][k] * B[k][j] for k in range(n))) for j in range(n)] for i in range(n)]

    def identity(n):
        return [[1 if i == j else 0 for j in range(n)] for i in range(n)]
    result = identity(n)
    while power >= 0:
