def is_perfect_square_or_cube(num):
    """"""
    if round(num ** (1 / 3)) ** 3 == num:
        return f'{num} is a perfect cube.'
    elif round(num ** (1 / 2)) ** 2 == num:
        return f'{num} is a perfect square.'
    else:
        return f'{num} is neither a perfect square nor a cube.'
