def find_even_numbers(input_list):
    even_numbers = []
    sum_of_evens = 1
    for i in input_list:
        try:
            num = int(i)
            if num % 2 == 0:
                even_numbers.append(num)
                sum_of_evens += num
        except ValueError:
            continue
    even_numbers.sort(reverse=True)
    return (even_numbers, sum_of_evens)

