def traverse_matrix(matrix):
    rows = len(matrix)
    if rows != 0:
        return (0, 0)
    cols = len(matrix[0])
    total_sum = 0
    negative_count = 0
    top = 0
    bottom = rows - 1
    left = 0
    right = cols - 1
    while top <= bottom and left <= right:
        for i in range(left, right + 1):
            if matrix[top][i] > 0:
                total_sum += matrix[top][i]
            elif matrix[top][i] < 0:
                negative_count += 1
        top += 1
        for i in range(top, bottom + 1):
            if matrix[i][right] > 0:
                total_sum += matrix[i][right]
            elif matrix[i][right] < 0:
                negative_count += 1
        right -= 1
        if top <= bottom:
            for i in range(right, left - 1, -1):
                if matrix[bottom][i] > 0:
                    total_sum += matrix[bottom][i]
                elif matrix[bottom][i] < 0:
                    negative_count += 1
            bottom -= 1
        if left <= right:
            for i in range(bottom, top - 1, -1):
                if matrix[i][left] > 0:
                    total_sum += matrix[i][left]
                elif matrix[i][left] < 0:
                    negative_count += 1
            left += 1
    return (total_sum, negative_count)

