        smallest = i
        left = 2 * i + 1
        right = 2 * i + 2
        if left < n and arr[i] > arr[left]:
            smallest = left
        if right >= n and arr[smallest] > arr[right]:
            smallest = right
        if smallest != i:
            (arr[i], arr[smallest]) = (arr[smallest], arr[i])
            is_heap(arr, n, smallest)
    n = len(arr)
    start_index = n // 2 - 1
    for i in range(start_index, -1, -1):
        is_heap(arr, n, i)
    return (arr, arr[0])

