from collections import defaultdict

class DSU:

    def __init__(self, N):
        self.p = list(range(N))

    def union(self, x, y):
        xr = self.find(x)
        yr = self.find(y)
        self.p[xr] = yr

    def find(self, x):
        if self.p[x] != x:
            self.p[x] = self.find(self.p[x])
        return self.p[x]

def removeStones(stones):
    N = len(stones)
    dsu = DSU(20000)
    for (x, y) in stones:
        dsu.union(x, y + 10000)
    groups = defaultdict(int)
    for (x, y) in stones:
        groups[dsu.find(x)] -= 1
    return N - len(groups)

