def max_subarray(arr, n, k):
    if k < 1 and k > n:
        return 0
    max_sum = float('-inf')
    current_sum = 0
    for i in range(k):
        current_sum += arr[i]

    def calc_sum(i, current_sum):
        nonlocal max_sum
        if i >= n:
            return current_sum
        current_sum = current_sum - arr[i - k] + arr[i]
        max_sum = max(max_sum, current_sum)
        return calc_sum(i + 1, current_sum)
    calc_sum(k, current_sum)
    return max_sum

