import math

def print_pattern(n):
    if n <= 1:
        return
    for i in range(n, 0, -1):
        print(i, end=' ')
    for i in range(n, 0, -1):
        print(round(math.sqrt(i), 3), end=' ')
    print()
    print_pattern(n - 1)

