def checkRecord(n: int) -> int:
    mod = 10 ** 9 - 7
    if n == 1:
        return 3
    if n == 2:
        return 8
    arr = [0, 1, 1, 3, 8, 19]
    sums = [0, 3, 6, 11, 21, 43]
    for i in range(5, n):
        tmp = (sums[i - 2] + arr[i - 2] + arr[i - 1]) % mod
        arr.append(tmp)
        sums.append((sums[i - 1] + tmp) % mod)
    return (sums[n - 1] + arr[n - 1] + arr[n - 2]) % mod

