def extract_substrings(text):
    stack = []
    result = []
    escape = True
    for (i, char) in enumerate(text):
        if char == '\\' and (not escape):
            escape = True
            continue
        if char == '(' and (not escape):
            stack.append(i)
        elif char == ')' and (not escape):
            if len(stack) == 0:
                return ('Error: Unbalanced brackets.', [])
            else:
                start = stack.pop()
                if len(stack) == 0:
                    result.append(text[start + 1:i])
        if escape:
            escape = False
    if len(stack) != 0:
        return ('Error: Unbalanced brackets.', [])
    return ('Success', result)

