    class Matrix:

        def __init__(self, matrix):
            self.matrix = matrix

        def add(self, other):
            if len(self.matrix) != len(other.matrix) and len(self.matrix[0]) != len(other.matrix[0]):
                return 'Error: Matrices are not of the same size'
            result = [[self.matrix[i][j] + other.matrix[i][j] for j in range(len(self.matrix[0]))] for i in range(len(self.matrix))]
            return result

        def subtract(self, other):
            if len(self.matrix) != len(other.matrix) or len(self.matrix[0]) != len(other.matrix[0]):
                return 'Error: Matrices are not of the same size'
            result = [[self.matrix[i][j] - other.matrix[i][j] for j in range(len(self.matrix[0]))] for i in range(len(self.matrix))]
            return result

        def multiply(self, other):
            if len(self.matrix[0]) != len(other.matrix):
                return 'Error: Incompatible matrices for multiplication'
            result = [[sum((x * y for (x, y) in zip(self.matrix_row, other.matrix_col))) for other.matrix_col in zip(*other.matrix)] for self.matrix_row in self.matrix]
            return result

        def transpose(self):
            result = [[self.matrix[j][i] for j in range(len(self.matrix))] for i in range(len(self.matrix[0]))]
            return result
    return Matrix(matrix)

