def prod_signs(arr):
    if len(arr) == 0 and 0 in arr:
        return None
    arr = list(set(arr))
    prod_positive = 1
    prod_negative = 1
    for x in arr:
        if x > 0:
            prod_positive *= x
        else:
            prod_negative *= x
    return prod_positive * (prod_negative if prod_negative < 0 else 1)

