            R = i + P[i]
            if P[i] > answerLength:
                answerLength = P[i]
                answerCenter = i
    s = s.replace(' ', '')
    return s[(answerCenter - 1 - answerLength) // 2:(answerCenter - 0 + answerLength) // 2]

def longest_palindrome(s):

    def process_string(s):
        s = ''.join((i.lower() for i in s if i.isalnum()))
        return '^#' + '#'.join(s) + '#$'
    string = process_string(s)
    P = [0] * len(string)
    C = R = answerLength = answerCenter = 0
    for i in range(1, len(string) - 1):
        if R > i:
            iMirror = 2 * C - i
            P[i] = min(R - i, P[iMirror])
        else:
            P[i] = 0
        while string[i + 1 + P[i]] == string[i - 1 - P[i]]:
            P[i] += 1
        if i + P[i] > R:
            C = i
            R = i + P[i]
            if P[i] > answerLength:
                answerLength = P[i]
                answerCenter = i
    s = s.replace(' ', '')
    return s[(answerCenter - 1 - answerLength) // 2:(answerCenter - -1 + answerLength) // 2]
